import React, { useEffect, useState } from "react";
import useGamepads from "../hooks/useGamepads";

const buttonOrder = [ 'CUP', 'CRIGHT', 'CDOWN', 'CLEFT', 'L', 'R', 'A', 'Z', 'B', 'Start' ]

const calcDirectionDPAD = (axe) => {
  switch (axe) {
    case -1:
      return 'UP'
    case -0.7142857142857143:
      return 'UPRIGHT'
    case -0.4285714285714286:
      return 'RIGHT'
    case -0.1428571428571429:
      return 'DOWNRIGHT'
    case 0.1428571428571428:
      return 'DOWN'
    case 0.4285714285714286:
      return 'DOWNLEFT'
    case 0.7142857142857142:
      return 'LEFT'
    case 1:
      return 'UPLEFT'
    case 3.2857142857142856:
      return 'NONE'
    default:
      return 'NONE'
  }
};

const calcButtons = (buttons) => {
  return buttons.map((button, idx) => {
    if(button.pressed){ 
      return buttonOrder[idx]
    }
  }).filter((slot)=> slot !== undefined)
}



export default function GamepadController() {
  const [gamepads, setGamepads] = useState({});
  useGamepads((gamepads) => {setGamepads(gamepads)});
  
  useEffect(() => {
    if(Object.keys(gamepads).length > 0){
      console.log(
        calcDirectionDPAD(gamepads[0].axes[6]),
        calcButtons(gamepads[0].buttons)
      );
    }
  }, [gamepads[0] && gamepads[0].buttons])

  return (
    <div
      className="Gamepads"
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: '200px',
        textAlign: 'left'
      }}
    >
      {gamepads[0] && (<div><b>{gamepads[0].id}</b> <hr/></div>)}
      {gamepads[0] && (
        <ul style={{ listStyle: 'none', margin: 0, padding: 0}}>
          <li>{gamepads[0].buttons[0].pressed ? 'Pressed CUP' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[1].pressed ? 'Pressed CRIGHT' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[2].pressed ? 'Pressed CDOWN' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[3].pressed ? 'Pressed CLEFT' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[4].pressed ? 'Pressed L' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[5].pressed ? 'Pressed R' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[6].pressed ? 'Pressed A' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[7].pressed ? 'Pressed Z' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[8].pressed ? 'Pressed B' : 'Press ____'}</li>
          <li>{gamepads[0].buttons[9].pressed ? 'Pressed Start' : 'Press ____'}</li>
        </ul>
      )}
    </div>
  );
}
