import React, { useEffect, useState } from "react";
import useGamepads from "../hooks/useGamepads";

const buttonOrder = [ 'CUP', 'CRIGHT', 'CDOWN', 'CLEFT', 'L', 'R', 'A', 'Z', 'B', 'Start' ]

const calcDirectionDPAD = (axe) => {
  switch (axe) {
    case -1:
      return 'UP'
    case -0.7142857142857143:
      return 'UPRIGHT'
    case -0.4285714285714286:
      return 'RIGHT'
    case -0.1428571428571429:
      return 'DOWNRIGHT'
    case 0.1428571428571428:
      return 'DOWN'
    case 0.4285714285714286:
      return 'DOWNLEFT'
    case 0.7142857142857142:
      return 'LEFT'
    case 1:
      return 'UPLEFT'
    case 3.2857142857142856:
      return 'NONE'
    default:
      return 'NONE'
  }
};

const calcButtons = (buttons) => {
  buttons.map((button, idx) => {
    // console.log(button)
    if(button.touched){ 
      console.log(buttonOrder[idx])
    }
  })
  // return buttons.filter((button, indx) => button.touched)
}



export default function GamepadController() {
  const [gamepads, setGamepads] = useState({});
  useGamepads((gamepads) => {setGamepads(gamepads)});
  
  useEffect(() => {
    if(gamepads[0]) {
      let player1DPAD = gamepads[0].axes[6]
      let player1buttons = gamepads[0].buttons
      // let player1Buttons = players.player1.buttons.reduce((accumulator = [], currentValue = '', index ) => {
      //   if(currentValue.pressed === true) {
      //     accumulator.push(index)
      //   }
      //   return accumulator
      // })

      console.log(
        // calcDirectionDPAD(player1DPAD),
        calcButtons(player1buttons)
      );
    }  
  }, [gamepads[0]])

  return (
    <div
      className="Gamepads"
      style={{ position: "fixed", bottom: 0, right: 0 }}
    >
      {gamepads[0] && (`id: ${gamepads[0].id} `)}
      buttons: {gamepads[0] && (
        <ul>
          <li>{gamepads[0].buttons[0].pressed ? 'Pressed 0' : 'CUP'}</li>
          <li>{gamepads[0].buttons[1].pressed ? 'Pressed 1' : 'CRIGHT'}</li>
          <li>{gamepads[0].buttons[2].pressed ? 'Pressed 2' : 'CDOWN'}</li>
          <li>{gamepads[0].buttons[3].pressed ? 'Pressed 3' : 'CLEFT'}</li>
          <li>{gamepads[0].buttons[4].pressed ? 'Pressed 4' : 'L'}</li>
          <li>{gamepads[0].buttons[5].pressed ? 'Pressed 5' : 'R'}</li>
          <li>{gamepads[0].buttons[6].pressed ? 'Pressed 6' : 'A'}</li>
          <li>{gamepads[0].buttons[7].pressed ? 'Pressed 7' : 'Z'}</li>
          <li>{gamepads[0].buttons[8].pressed ? 'Pressed 8' : 'B'}</li>
          <li>{gamepads[0].buttons[9].pressed ? 'Pressed 9' : 'Start'}</li>
        </ul>
      )}
      
      
    </div>
  );
}
