import React, { useEffect, useState } from "react";
import useGamepads from "../hooks/useGamepads";
import N64Controller from "../utils/N64Controller";

export default function GamepadController() {
  const [gamepads, setGamepads] = useState({});
  useGamepads((gamepads) => {setGamepads(gamepads)});  
  return (
    <div
      className="Gamepads"
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: '200px',
        textAlign: 'left'
      }}
    >
      {gamepads[0] && (<div><b>{gamepads[0].id}</b> <hr/></div>)}
      {gamepads[0] && (
        <div style={{
          width: '100px',
          height: '100px',
          background: 
            N64Controller(gamepads[0]).dpad === 'UP'
            ? 'red'
            : N64Controller(gamepads[0]).dpad === 'RIGHT'
              ? 'green'
              : N64Controller(gamepads[0]).dpad === 'DOWN'
                ? 'blue'
                : N64Controller(gamepads[0]).dpad === 'DOWN'
                  ? 'yellow'
                  : 'black'
        }}/>)}
    </div>
  );
}
