import React, { useEffect, useState } from "react";
import useGamepads from "../hooks/useGamepads";
import GamepadSvg from "./GamepadSvg";

export default function GamepadController() {
  const [gamepads, setGamepads] = useState({});
  useGamepads((gamepads) => setGamepads(gamepads));
  let Player1 = gamepads[0];
  let Player1DPAD = Player1.axes[6]
  
  const calcDirectionDPAD = (axe) => {
    switch (axe) {
      case -1:
        return 'UP'
      case -0.7142857142857143:
        return 'UPRIGHT'
      case -0.4285714285714286:
        return 'RIGHT'
      case -0.1428571428571429:
        return 'DOWNRIGHT'
      case 0.1428571428571428:
        return 'DOWN'
      case 0.4285714285714286:
        return 'DOWNLEFT'
      case 0.7142857142857142:
        return 'LEFT'
      case 1:
        return 'UPLEFT'
      case 3.2857142857142856:
        return 'NONE'
      default:
        return 'NONE'
    }
  };

  useEffect(() => {
    setPlayer({
      player1: gamepads[0],
      player2: gamepads[1],
      player3: gamepads[2],
      player4: gamepads[3],
    })
  }, [gamepads[0] && gamepads[0].buttons[9].pressed, 
      gamepads[1] && gamepads[1].buttons[9].pressed, 
      gamepads[2] && gamepads[2].buttons[9].pressed, 
      gamepads[3] && gamepads[3].buttons[9].pressed, ])

  
  useEffect(() => {
    if(gamepads[0]) {
      set
      console.log(
        calcDirectionDPAD(Player1DPAD),
      );

    //   console.log({axes: gamepads[0].axes})

    //   if(gamepads[0].axes[6] === 3.2857142857142856) {
    //     console.log('DPAD-NONE')
    //   }
    //   if(gamepads[0].axes[6] === 0.1428571428571428) {
    //     console.log('DPAD-DOWN')
    //   }
    //   if(gamepads[0].axes[6] === -1) {
    //     console.log('DPAD-UP')
    //   }
    //   if(gamepads[0].axes[6] === -0.4285714285714286) {
    //     console.log('DPAD-RIGHT')
    //   }
    //   if(gamepads[0].axes[6] === 0.4285714285714286) {
    //     console.log('DPAD-LEFT')
    //   }
    }
    
  }, [gamepads[0] && gamepads[0].buttons[0].pressed])

  return (
    <div
      className="Gamepads"
      style={{ position: "fixed", bottom: 0, right: 0 }}
    >
      <small>Gamepads</small>
      {gamepads[0] && (`id: ${gamepads[0].id} `)}
      buttons: {gamepads[0] && (
        <>
          <div>{gamepads[0].buttons[0].pressed ? 'Pressed 0' : 'CUP'}</div>
          <div>{gamepads[0].buttons[1].pressed ? 'Pressed 1' : 'CRIGHT'}</div>
          <div>{gamepads[0].buttons[2].pressed ? 'Pressed 2' : 'CDOWN'}</div>
          <div>{gamepads[0].buttons[3].pressed ? 'Pressed 3' : 'CLEFT'}</div>
          <div>{gamepads[0].buttons[4].pressed ? 'Pressed 4' : 'L'}</div>
          <div>{gamepads[0].buttons[5].pressed ? 'Pressed 5' : 'R'}</div>
          <div>{gamepads[0].buttons[6].pressed ? 'Pressed 6' : 'A'}</div>
          <div>{gamepads[0].buttons[7].pressed ? 'Pressed 7' : 'Z'}</div>
          <div>{gamepads[0].buttons[8].pressed ? 'Pressed 8' : 'B'}</div>
          <div>{gamepads[0].buttons[9].pressed ? 'Pressed 9' : 'Start'}</div>
        
          {/* <div>{gamepads[0].buttons[0].pressed ? 'Pressed 0' : 'CUP'}</div>
          <div>{gamepads[0].buttons[1].pressed ? 'Pressed 1' : 'CRIGHT'}</div>
          <div>{gamepads[0].buttons[2].pressed ? 'Pressed 2' : 'CDOWN'}</div>
          <div>{gamepads[0].buttons[3].pressed ? 'Pressed 3' : 'CLEFT'}</div>
          <div>{gamepads[0].buttons[4].pressed ? 'Pressed 4' : 'L'}</div>
          <div>{gamepads[0].buttons[5].pressed ? 'Pressed 5' : 'R'}</div>
          <div>{gamepads[0].buttons[6].pressed ? 'Pressed 6' : 'A'}</div>
          <div>{gamepads[0].buttons[7].pressed ? 'Pressed 7' : 'Z'}</div>
          <div>{gamepads[0].buttons[8].pressed ? 'Pressed 8' : 'B'}</div>
          <div>{gamepads[0].buttons[9].pressed ? 'Pressed 9' : 'Start'}</div> */}
        </>
      )}
      
      {/* {gamepadDisplay} */}
      {/* {gamepads && gamepads[0] && (
        <>
          <GamepadSvg
            directionUp={gamepads[0].buttons[12].pressed}
            directionDown={gamepads[0].buttons[13].pressed}
            directionLeft={gamepads[0].buttons[14].pressed}
            directionRight={gamepads[0].buttons[15].pressed}
            buttonDown={gamepads[0].buttons[0].pressed}
            buttonRight={gamepads[0].buttons[1].pressed}
            buttonLeft={gamepads[0].buttons[2].pressed}
            buttonUp={gamepads[0].buttons[3].pressed}
            select={gamepads[0].buttons[8].pressed}
            start={gamepads[0].buttons[9].pressed}
            analogLeft={
              gamepads[0].axes[0] > 0.3 ||
              gamepads[0].axes[0] < -0.3 ||
              gamepads[0].axes[1] > 0.3 ||
              gamepads[0].axes[1] < -0.3
            }
            analogRight={
              gamepads[0].axes[2] > 0.3 ||
              gamepads[0].axes[2] < -0.3 ||
              gamepads[0].axes[3] > 0.3 ||
              gamepads[0].axes[3] < -0.3
            }
            analogLeftDirection={[
              calcDirectionHorizontal(gamepads[0].axes[0]),
              calcDirectionVertical(gamepads[0].axes[1])
            ]}
            analogRightDirection={[
              calcDirectionHorizontal(gamepads[0].axes[2]),
              calcDirectionVertical(gamepads[0].axes[3])
            ]}
          />
          <h3>Player 1</h3>
        </>
      )} */}
    </div>
  );
}
