import React, { useEffect, useState } from "react";
import useGamepads from "../hooks/useGamepads";
import GamepadSvg from "./GamepadSvg";

const calcDirectionDPAD = (axe) => {
  switch (axe) {
    case -1:
      return 'UP'
    case -0.7142857142857143:
      return 'UPRIGHT'
    case -0.4285714285714286:
      return 'RIGHT'
    case -0.1428571428571429:
      return 'DOWNRIGHT'
    case 0.1428571428571428:
      return 'DOWN'
    case 0.4285714285714286:
      return 'DOWNLEFT'
    case 0.7142857142857142:
      return 'LEFT'
    case 1:
      return 'UPLEFT'
    case 3.2857142857142856:
      return 'NONE'
    default:
      return 'NONE'
  }
};

const calcButtons = (buttons) => {
  buttons.filter((button) => {
    if(button.pressed === true) {
      return button
    }
  })
